sys_ui_script.script:
var app = angular.module('collectit',[]);
app.controller('CollectIt',function($scope,$http){

	$scope.collectits = [];
	load();

	function load(){
		$http.get("<endpoint from REST API Explorer>")
		.then(function(response){
			for(var i = 0 ; i < response.data.result.length ; i++){
				var retObj = {};
				retObj.number = response.data.result[i].number;
				retObj.short_description = "";
				retObj.image = '';
				$scope.collectits.push(retObj);
			}
		},
			 function(response){
			     alert("Something did not work right.  The records did not load.");
		});
	}

});
